mapper.select:
SELECT MEMBER_NUMBER
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT COUNT(MEMBER_EMAIL)
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT MEMBER_NUMBER, MEMBER_EMAIL, MEMBER_NICKNAME, MEMBER_INTERESTS, MEMBER_INTRODUCTION, MEMBER_PROFILE
FROM TBL_MEMBER
WHERE MEMBER_NUMBER = #{memberNumber}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_NICKNAME = #{memberNickname}, MEMBER_INTRODUCTION = #{memberIntroduction}
WHERE MEMBER_NUMBER = #{memberNumber}
